#include<iostream>#include <fstream>#include<cstdio>#include<vector>#include<string>#include "hash.h"#include <utility>#include <assert.h>#include <ctype.h>#include "ir/ir.h"//#include "ir/wire.h"//#include "ir/instruction.h"//#include "ir/memory.h"//#include "asem.h"#define FR(a,b) for(typeof(b.begin()) a=b.begin();a!=b.end();++a)using namespace std;static string reserved[] ={  "rule",  "stage","false","true","enum","const","width",  "size","type","index","read","write","wire","pipe_line",  "register","like","override","alias","function_unit",  "instruction","top","select","code","do","binary","switch"};static int string2int(string & s){  int r(0);  for(int i=0;i<s.length();++i)    r=r*10+s[i]-'0';  return r;}hash_control hc_unfold_read;vector<vector<triple > > unfolded_list_read;vector<string> unfolded_list_name_read;// name of rule of corresponding unfolded_listclass hash_control hc_read;class hash_control & Asem::hc_unfold=hc_unfold_read;vector<vector<triple > > & Asem::unfolded_list=unfolded_list_read;vector<string> & Asem::unfolded_list_name=unfolded_list_name_read;// name of rule of corresponding unfolded_listclass hash_control & Asem::hc=hc_read;int main(){  FILE * input=fopen("in.txt","r");  Asem asem;  asem.type=type_is_vector;  // 读入文件  asem.gen(input);    //asem.display(0);  // 把保留字加入hash表  for(int i=0;i<sizeof(reserved)/sizeof(reserved[0]);++i)    asem.hc.insert(reserved[i].c_str(),(void*)reserved);    // 插入hash表  asem.dfs_insert_hash("");  // instruction里必须有顶层规则  //string instr="...instruction.top";  //assert(hc.find(instr)!=0);  //找到instruction那一项  Asem &instruction=*(asem.ivec[0].find((string)"instruction"));  //找到instruction中的top  Asem &top=*(instruction.find((string)"top"));  Asem &rule=*instruction.find(top.ivec[1].name);  //查看结果  ofstream yout("yout.y");//.y file name  ofstream dot_c_out("cout.cpp");// .c file name;  ofstream dot_h_out("cout.h");  int allrule=rule.unfold(yout,dot_c_out,dot_h_out);  cout<<rule.unfolded_list[allrule].size()<<endl;  fstream out_txt("out.txt");  hash_control tmp;  for(int i=0;i<rule.unfolded_list[allrule].size();++i)    {      out_txt<<i<<endl<<"\tcode: "<<rule.unfolded_list[allrule][i].code<<endl  	     <<"\tbinary: "<<rule.unfolded_list[allrule][i].binary<<endl;      out_txt<<"do: "<<endl;      // for(int i=0;i<rule.unfolded_list[allrule][i].doo.size();++i)      // 	out_txt<<rule.unfolded_list[allrule][i].doo[i]<<' ';      out_txt<<endl;      for(int i=0;i<rule.unfolded_list[allrule][i].off_in_code.size();++i)  	out_txt<<rule.unfolded_list[allrule][i].off_in_code[i]<<' '<<rule.unfolded_list[allrule][i].off_in_binary[i]<<' ';      out_txt<<endl;    }  //接下来是输出到为ir  //处理wire描述  Ir ir;  Asem &asem_wire=*asem.ivec[0].find((string)"wire");  assert(asem_wire.type==type_is_vector);  Asem & asem_type=*asem.ivec[0].find("type");  assert(asem_type.type==type_is_vector);  for(int i=1;i<asem_wire.ivec.size();++i)    {      string wire_type_name=(*asem_wire.ivec[i].find((string)"width")).ivec[1].name;      //TODO 没有检查type对应的数据类型应该是无符号，查找过程加入出错检查      int wire_width=string2int((*(*(asem_type.find(wire_type_name))).find((string) "width")).ivec[1].name);      ir.add_wire(Wire(asem_wire.ivec[i].ivec[0].name,wire_width));    }  //add type info to ir  for(int i=1;i<asem_type.ivec.size();++i)    {      ir.add_type(Type(asem_type.ivec[i].ivec[0].name,		       string2int((*asem_type.ivec[i].find("width")).ivec[1].name),		       (*asem_type.ivec[i].find("flag")).ivec[1].name));    }  //add stage info to ir  Asem & asem_stage=*asem.ivec[0].find("stage");  assert(asem_stage.type==type_is_vector);  for(int i=1;i<asem_stage.ivec.size();++i)    {      ir.add_stage(Stage(asem_stage.ivec[i].ivec[0].name));    }  //add enum info to ir  Asem & asem_enum=*asem.ivec[0].find("enum");  assert(asem_enum.type==type_is_vector);  for(int i=1;i<asem_enum.ivec.size();++i)    {      ir.add_enum(asem_enum.ivec[i].ivec[0].name);      for(int j=1;j<asem_enum.ivec[i].ivec.size();++j)	if(asem_enum.ivec[i].ivec[j].is_string())	  ir.add_enum_entry(asem_enum.ivec[i].ivec[j].name,			    asem_enum.ivec[i].ivec[j].name);	else	  ir.add_enum_entry(asem_enum.ivec[i].ivec[j].ivec[0].name,			    asem_enum.ivec[i].ivec[j].ivec[1].name);    }  //add memory info to ir  Asem & asem_memory=*asem.ivec[0].find("memory");  assert(asem_memory.type==type_is_vector);  for(int i=1;i<asem_memory.ivec.size();++i)    {      string sizes=(*asem_memory.ivec[i].find("size")).ivec[1].name;      int sizei;      if(sizes[0]<='9' && sizes[0]>='0')	sizei=string2int(sizes);      else//TODO need check here	sizei=string2int((*(*asem.ivec[0].find(sizes)).find("value")).ivec[1].name);      ir.add_memory(Memory(asem_memory.ivec[i].ivec[0].name,			   sizei,			   (*asem_memory.ivec[i].find("type")).ivec[1].name,			   (*asem_memory.ivec[i].find("index")).ivec[1].name));    }  //add register info to ir  Asem & asem_register=*asem.ivec[0].find("register");  assert(asem_register.type==type_is_vector);  for(int i=1;i<asem_register.ivec.size();++i)    {      string name=asem_register.ivec[i].ivec[0].name;      int size;      if((asem_register.ivec[i].find("size"))!=NULL)	size=string2int((*asem_register.ivec[i].find("size")).ivec[1].name);      else size=1;      string type=(*asem_register.ivec[i].find("type")).ivec[1].name;      string idx;      if(asem_register.ivec[i].find("index")!=NULL)	idx=(*asem_register.ivec[i].find("index")).ivec[1].name;      string alias;      if((asem_register.ivec[i].find("alias"))!=NULL)	alias=(*asem_register.ivec[i].find("alias")).ivec[1].name;      string read_port;      if((asem_register.ivec[i].find("read"))!=NULL)	read_port=(*asem_register.ivec[i].find("read")).ivec[1].name;      string write_port;      if((asem_register.ivec[i].find("write"))!=NULL)	write_port=(*asem_register.ivec[i].find("write")).ivec[1].name;      ir.add_register(Register(name,size,type,idx,alias,read_port,write_port));    }  //add pipeline info to ir  Asem & asem_pipeline=*asem.ivec[0].find("pipe_line");  assert(asem_pipeline.type==type_is_vector);  for(int i=1;i<asem_pipeline.ivec.size();++i)    {      ir.add_pipeline(Pipeline(asem_pipeline.ivec[i].ivec[0].name,			       (*asem_pipeline.ivec[i].find("width")).ivec[1].name));    }  ofstream out2_txt("out2.txt");  // 把code和binary, do, argument list 描述加入到ir里  for(int i=0;i<=allrule;++i)    for(vector<triple>::iterator ite=rule.unfolded_list[i].begin();	ite!=rule.unfolded_list[i].end();++ite)      {	vector<pp> tmp;	for(int i=0;i<ite->off_in_code.size();++i)  	tmp.push_back(pp(ite->off_in_code[i],ite->off_in_binary[i]));	ir.add_instruction(ite->rulename,ite->code,ite->binary,ite->doo,tmp,ite->enum_name,ite->arg_list);    }  // ir.output_wire(out2_txt);  // ir.output_type(out2_txt);  // ir.output_stage(out2_txt);  // ir.output_enum(out2_txt);  // ir.output_memory(out2_txt);  // ir.output_register(out2_txt);  // ir.output_pipeline(out2_txt);  // ir.output_instruction_set(out2_txt);  ir.output_all(out2_txt);  return 0;}